INSERT INTO `recettes` (`id`, `nom`, `annee`, `ingrédient`, `description`, `affiche`) VALUES
(1, 'Bûche de Noël au chocolat et à la crème de marrons', 2023, '4 œufs\r\n150 g de sucre\r\n100 g de farine\r\n30 g de cacao en poudre\r\n1 cuillère à café de levure chimique\r\n200 g de crème de marrons\r\n200 ml de crème fraîche\r\n150 g de chocolat noir', 'Préchauffez le four à 180°C.\r\n\r\nDans un bol, battez les œufs avec le sucre jusqu\'à obtenir un mélange mousseux.\r\n\r\nAjoutez la farine, le cacao en poudre et la levure. Mélangez délicatement.\r\n\r\nVersez la pâte sur une plaque de cuisson recouverte de papier sulfurisé et étalez-la uniformément.\r\n\r\nEnfournez pendant environ 12-15 minutes. Une fois cuit, roulez le gâteau dans un linge humide et laissez-le refroidir.\r\nFaites fondre le chocolat noir au bain-marie.\r\n\r\nDans un bol, mélangez la crème de marrons avec la moitié de la crème fraîche.\r\nAjoutez le chocolat fondu.\r\nDéroulez le gâteau refroidi, étalez la crème de marrons et roulez à nouveau.\r\n\r\nRéfrigérez pendant quelques heures avant de servir.', ),
(2, 'Dinde rôtie aux herbes de Provence', 2023, '1 dinde de taille appropriée\r\nSel et poivre\r\n3 cuillères à soupe d\'huile d\'olive\r\n2 cuillères à soupe d\'herbes de Provence\r\n4 gousses d\'ail émincées\r\n1 oignon coupé en quartiers\r\n1 citron coupé en tranches', 'Préchauffez le four à 180°C.\r\n\r\nAssaisonnez la dinde avec du sel et du poivre, puis badigeonnez-la d\'huile d\'olive.\r\n\r\nSaupoudrez d\'herbes de Provence et frottez l\'ail émincé sur la dinde.\r\nPlacez les quartiers d\'oignon et les tranches de citron à l\'intérieur de la dinde.\r\n\r\nPlacez la dinde dans un plat de cuisson et faites-la rôtir au four pendant environ 2 à 3 heures, en la badigeonnant régulièrement avec le jus de cuisson.\r\nLaissez reposer la dinde avant de la découper.\r\n', );
